import { Component, Input, ChangeDetectionStrategy, ChangeDetectorRef, ViewChild, ElementRef }
    from '@angular/core';
import { AnimationEvent } from '@angular/animations';
import * as moment from 'moment';
import 'rxjs/add/operator/takeUntil';
import { Subject } from 'rxjs/Subject';

import { AlarmService } from '../../alarms/alarm.service';
import { PlatformService } from '../../platforms/platform.service';
import { AlarmMapService } from '../../map/alarms/alarmMap.service';
import { Alarm } from '../../alarms/alarm.class';
import { slideDown } from '../../shared/animations';
import { MapViewOptions } from '../../shared/map-view-options.class';
import { Image } from '../../shared/shared-interfaces';

@Component({
    selector: 'alarm-list-item',
	templateUrl: 'alarm-list-item.component.html',
    animations: [
        slideDown
    ],
    changeDetection: ChangeDetectionStrategy.OnPush
})

export class AlarmListItem {
    @Input() alarm: Alarm;
	@Input() multiSelect: boolean;
	@Input() mapViewOptions: MapViewOptions;
    @Input() Selected: boolean;
	@Input() Expanded: boolean;
    prevent: boolean;
    delay: number;
    timer: NodeJS.Timer;
	actionMenuOpen: boolean;
	priorityName: string;
	priorityIcon: string;
	platformName: string;
	alarmDescription: string;

    private ngUnsubscribe: Subject<void> = new Subject<void>();

    constructor(private alarmService: AlarmService, private mapService: AlarmMapService, private platformService: PlatformService,
        private changeDetectorRef: ChangeDetectorRef) {
        this.actionMenuOpen = false;

        // Click -> Dbl Click facilitation
        this.prevent = false;
        this.delay = 200;
		this.timer = null;      
	}

	ngOnInit(): void {
		this.priorityName = this.alarmService.convertPriorityName(this.alarm.Priority);
		this.priorityIcon = this.getPriorityIconSrc();
		this.alarmDescription = this.alarm.getDescription();
		this.platformName = this.getPlatformDisplayName();
	}

    ngAfterViewInit(): void {
        this.alarmService.refreshTimerSub
            .takeUntil(this.ngUnsubscribe)
            .subscribe({
                next: () => this.changeDetectorRef.detectChanges()
            });
        this.alarmService.editedAlarm
            .takeUntil(this.ngUnsubscribe)
			.subscribe({
                next: (alarm) => {
                    if (alarm.Id === this.alarm.Id) {
                        this.changeDetectorRef.detectChanges();
                    }
                }
			});
		this.platformService.onPlatformsLoaded
			.takeUntil(this.ngUnsubscribe)
			.subscribe({
				next: () => this.changeDetectorRef.detectChanges()
            });
        this.platformService.onEditedPlatform
            .takeUntil(this.ngUnsubscribe)
            .subscribe({
                next: (platform) => {
                    if (this.alarm.PlatformId === platform.id) {
                        this.changeDetectorRef.detectChanges();
                    }
                }
            });
    }

    getPriorityIconSrc(): string {
        //TODO convert to file locations
        switch (this.alarm.Priority) {
            case 1:
                return 'data:image/png;base64,';
            case 2:
                return 'data:image/png;base64,';
            case 3:
                return 'data:image/png;base64,';
            default:
                return 'data:image/png;base64,';
        }
    }

	getPlatformDisplayName(): string {
		if (this.alarm.PlatformId) {
			let platform = this.platformService.getPlatform(this.alarm.PlatformId);
			if (platform && platform.DisplayName) {
				return platform.DisplayName;
			} else {
				return this.alarm.PlatformId;
			}
		}

		return null;
    }

    getPlatformStatusClass(): string {
        if (this.alarm.PlatformId) {
            let platform = this.platformService.getPlatform(this.alarm.PlatformId);

            if (platform) {
                return this.platformService.getPlatformStatusClass(platform);
            }
        }
    }

    getAlarmTime(): string {
        if (!this.alarm || !this.alarm.ReportedTime) {
			return;
		}

        let result = moment.duration(moment().diff(this.alarm.ReportedTime)).humanize();
		if (result.includes('second')) {
			return 'now';
		}
		result = result.replace('year', 'yr');
		result = result.replace('month', 'mth');
		result = result.replace('hour', 'hr');
		result = result.replace('minute', 'min');
		result = result.replace('an ', '1 ');
		result = result.replace('a ', '1 ');
		return result;
    }

    acknowledgeAlarms(): void {
        this.alarmService.acknowledgeAlarms(this.alarm);
    }

    clearAlarms(): void {
        this.alarmService.clearAlarmsWithConfirmation(this.alarm);
    }

    dismissAlarms(): void {
        this.alarmService.dismissAlarmsWithConfirmation(this.alarm);
    }

	toggleExpandedView(event: any): void {
	    event.stopPropagation();

        // If this alarm is expanded, close it and unset the current open alarm
        if (this.alarm.Expanded) {
            this.alarmService.setExpandedItem(null);
        } else {
            this.alarmService.setExpandedItem(this.alarm.Id);
        }
    }

    zoomTo(): void {
        clearTimeout(this.timer);
        this.prevent = true;

        this.mapService.zoomToAlarmMarker(this.mapService.getAlarmMarkerId(this.alarm));
        this.alarmService.selectOnlyAlarm(this.alarm.Id);
	}

	goToPlatform(): void {
		event.stopPropagation();
		this.prevent = true;
	
		this.platformService.showRobotMonitor(this.platformService.getPlatform(this.alarm.PlatformId));
	}

	selectAlarm(): void {
		if (this.alarm.Selected) {
			this.alarmService.deSelectAlarm(this.alarm.Id);
		} else {
			this.alarmService.selectAlarm(this.alarm.Id);
		}
	}

    toggleSelected(event: MouseEvent): void {
		// Delay click action to allow dblclick to occur
		this.timer = setTimeout(() => {
			if (!this.prevent) {
				// If action menu was clicked, do not select the alarm
				if (this.actionMenuOpen) {
					this.actionMenuOpen = false;
					return;
				}
				this.alarmService.handleClickAlarm(this.alarm, event);
			}
			this.prevent = false;
		}, this.delay);
    }

    openActionMenu(event: any): void {
        event.preventDefault();
        this.alarmService.openAlarmActionMenu(this.alarm, event);
    }
    
	hasAttachments(): boolean {
		if (this.alarm.Comments && this.alarm.Comments.length) {
			return true;
        }
        if (this.alarm.HasSnapshots) {
            return true;
        }
		return false;
    }

    ngOnDestroy(): void {
        this.ngUnsubscribe.next();
        this.ngUnsubscribe.complete();
    }
}